import axios from 'axios';
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { IconName, AiOutlineBars } from 'react-icons/ai';
import { BiSort } from 'react-icons/bi';

function Todo({ userObj }) {
  const [editing, setEditing] = useState(false);
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const navigate = useNavigate();
  const date = new Date();
  const month = date.getUTCMonth() + 1;
  const day = date.getUTCDate();
  const week = ['일', '월', '화', '수', '목', '금', '토'];
  const dayOfWeek = week[new Date('2016-07-28').getDay()];

  const getTasks = () => {
    axios
      .get('api/tasks')
      .then((res) => {
        setTasks(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    getTasks();
  }, [tasks]);

  const toggleEdit = () => {
    setEditing(true);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    const taskObj = {
      text: task,
      createdAt: Date.now(),
      email: userObj.email,
    };
    axios.post('/api/task', taskObj);
  };

  const onChange = (e) => {
    const { value } = e.target;
    setTask(value);
  };

  const onDeleteClick = (text) => {
    console.log(text);
    const ok = window.confirm('삭제하시겠습니까?');
    if (ok) {
      axios.delete('/api/task', { data: { text } });
    }
  };

  return (
    <>
      <div className="taskToolbar">
        <div className="taskToolbar-top">
          <div className="slideBtn">
            <AiOutlineBars size="18" />
          </div>
          <div className="title">
            <h2>오늘 할 일</h2>
          </div>
          <div className="action">
            <BiSort size="18" />
          </div>
        </div>
        <div className=''></div>
      </div>
      <div>
        <span>
        </span>
      </div>
      <div>
        {tasks.map((task, i) => (
          <div key={i}>
            <h4>{task.text}</h4>
            <button>edit</button>
            <button onClick={() => onDeleteClick(task.text)}>delete</button>
          </div>
        ))}
      </div>
      {!editing && (
        <>
          <button onClick={toggleEdit}>Add task</button>
        </>
      )}

      <div className="viewContent">
        {editing && (
          <form onSubmit={onSubmit}>
            <div className="editor">
              <div className="editorInput">
                <input value={task} onChange={onChange} type="text" />
              </div>
              <div className="editorBtn">
                <button type="button">Cancel</button>
              </div>
            </div>
            <button type="sumbit" onClick={toggleEdit}>
              Add
            </button>
          </form>
        )}
      </div>
    </>
  );
}

export default Todo;
