import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Todo({ isLoggedIn, setIsLoggedIn }) {
  const [editing, setEditing] = useState(false)
  const [task, setTask] = useState('')
  const [date, setDate] = useState(new Date().toDateString());
  const navigate = useNavigate();

  const onLogOutClick = () => {
    setIsLoggedIn(false);
    navigate('/');
  };

  const toggleEdit = () => {
    setEditing(prev => !prev)
    console.log(editing)
  }

  const onSubmit = (e) => {
    e.preventDefault()
    console.log('prevent')
  }

  const onChange = (editing) => {

  }


  return (
    <>
      <div className="headerContent">
        {!editing && (
          <>
            <span>Today</span>
            <span>{date}</span>
            <button onClick={toggleEdit}>Add task</button>
          </>
        )}
      </div>
      <div className="viewContent">
        <ul>
          <li>
            <form onSubmit={onSubmit}>
              {editing &&(
              <div className="editor">
                <div className="editorInput">
                  <textarea onChange={onChange} name="" id="" cols="30" rows="10"></textarea>
                </div>
              </div>
              )}

              {editing &&(
                <div className="editorBtn">
                  <button onClick={toggleEdit}>Cancel</button>
                  <button type='sumbit' onClick={toggleEdit}>Add task</button>
                </div>
              )}
            </form>
          </li>
        </ul>
      </div>
    </>
  );
}

export default Todo;
